mapper.update:
update message
set msgstatus ='Y'
where msgno = #{msgNo}

update message
set msgstatus ='D'
where msgno = #{msgNo}

mapper.select:
select * from message_view
where recmemno = #{recMemNo}
and msgstatus = 'D'

select count(*) from message_view
where msgstatus = 'N' 
and recmemno = #{recMemNo}
