mapper.update:
update payment
set hasFile=#{hasFile}
where docNo=#{docNo}

update payment
set formNo=#{formNo}, typeNo=#{typeNo}, title=#{title}, content=#{content}, PROGRESS=#{progress}
, keep=#{keep}, EXPIRYDATE=add_months(sysdate,#{keep}), WRITEDATE=sysdate, IMSY=#{imsy}
where docNo=#{docNo} and memNo=#{writememNo}

update paylistView
set gmemNo=NULL
where docNo=#{docNo}

update payment
set progress='cancled'
where docNo=#{docNo}

update payment
set imsy='Y'
where docNo=#{docNo}

mapper.select:
select * from paymentview

	imsy='Y'
	and memNo=#{identNum}

		and title=#{searchKeyword}


		and typeNo=#{searchCondition}


		and to_date(writedate)>=to_date(#{startDay})


		and to_date(writedate) < to_date(#{endDay})+1


		and sysdate < expirydate+1
		order by docNo desc

select * from (
	select * from PAYLISTVIEW

		docNo=#{docNo}
		and PAYDATE is null
		and IMSY != 'Y'

			and title=#{searchKeyword}


			and typeNo=#{searchCondition}


			and to_date(writedate)>=to_date(#{startDay})


			and to_date(writedate) < to_date(#{endDay})+1


			and sysdate < expirydate+1
			order by substr(gmemNo,3,3) 


)
where rownum=1

mapper.update:
update PAYMENTLINE
set PAYDATE=sysdate
where docNo=#{docNo} and memNo=#{memNo}

update paymentline
set read='Y'
where docNo=#{docNo} and memNo=#{memNo}

mapper.select:
select count(*) from PAYMENTLINE
where PAYDATE is null
and docNo=#{docNo}

mapper.update:
update payment
set PROGRESS=#{progress}
where docNo=#{docNo}

mapper.select:
select * from paylistView
where progress=#{progress}
and gmemNo=#{identNum}

	and title=#{searchKeyword}


	and typeNo=#{searchCondition}


	and to_date(WRITEDATE)>=to_date(#{startDay})


	and to_date(writedate) < to_date(#{endDay})+1


	and sysdate < expirydate+1
	order by docNo desc
