mapper.select:
select * from(
	select rownum as RNUM, A.*
	from (
	   	select * from v_resmanage
	    order by resname
	)A
)

where RNUM > #{firstRecordIndex}
 and RNUM <= #{firstRecordIndex } + #{recordCountPerPage}

mapper.update:
update resmanage
set resname=#{resName}

, resimage=#{resImage}, ressize=#{resSize}, resoriginalimage=#{resOriginalImage}

, reslocation=#{resLocation}, ressubdesc=#{resSubdesc}, rkNo=#{rkNo}
where resno=#{resNo}

mapper.select:
select * from(
	select rownum as RNUM, A.*
	from (
	   	select * from v_resreserve

			where rvstate=#{rvState}


			where rvstate='yes'
			or rvstate='no'

	   	order by rvstart
	)A
)

where RNUM > #{firstRecordIndex}
 and RNUM <= #{firstRecordIndex } + #{recordCountPerPage}

mapper.update:
update resreserve
set rvstate=#{text}
where rvNo=#{no}

update resreserve
set rvReReason=#{text}
where rvNo=#{no}

mapper.select:
select * from(
	select rownum as RNUM, A.*
	from (
		select * from v_resreserve
		where resno=#{searchNumber}

			and rvstate=#{rvState}

		)A
	)

where RNUM > #{firstRecordIndex}
 and RNUM <= #{firstRecordIndex } + #{recordCountPerPage}

